import React, { useEffect, useState } from "react";
import Clock from 'react-live-clock';
import { NavLink } from "react-router-dom";
import logo from '../images/favicon.png';
import "../index.css";
import { FcAlarmClock} from "react-icons/fc";
import {FaHome,FaPhoneSquareAlt,FaUserCheck} from "react-icons/fa";
import {FiMenu} from "react-icons/fi";
//FaUserTimes

const HomePage = () => {
  let count = 1;
  const [vegData, setVegData] = useState();

  const getAllData = async () => {
    try {
      const getData = await fetch(
        `${process.env.REACT_APP_BASE_URL}/getAllVegetables`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      const res = await getData.json();
      setVegData(res);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getAllData();
  },[]);
  console.log(vegData);

  return (
    <>

      <div className="flex justify-between text-center items-center p-[30px]">
        <img src={logo} alt="{logo}" className='w-[60px] h-[60px]'/>
        <h1 className="text-[#008000] text-[50px] font-bold">Farmer-Merchant Server</h1>
        <div className="flex items-center">
          <FcAlarmClock className="text-xl mr-[10px]"/>
          <Clock format={'hh:mm:ss A'} ticking={true} className='text-md'/>
          {/* <span className="text-2xl">{`${new Date().toLocaleString()}`}</span> */}
        </div>
      </div>

      <nav className="flex mt-[10px] bg-[#008000] font-bold text-[#f2f2f2] overflow-hidden">
        <span className='flex px-[16px] py-[14px] gap-[10px] items-center text-lg hover:bg-[#f2f2f2] hover:text-[#008000]'>
          <FaHome/>
          <NavLink to="/" className={({ isActive, isPending }) => isPending ? "pending" : isActive ? "active" : ""}> Home </NavLink>
        </span>
        <span className='flex px-[16px] py-[14px] gap-[10px] items-center text-lg hover:bg-[#f2f2f2] hover:text-[#008000]'>
          <FiMenu/>
          <NavLink to="/" className={({ isActive, isPending }) => isPending ? "pending" : isActive ? "active" : ""}> About </NavLink>
        </span>
        <span className='flex px-[16px] py-[14px] gap-[10px] items-center text-lg hover:bg-[#f2f2f2] hover:text-[#008000]'>
          <FaPhoneSquareAlt/>
          <NavLink to="/" className={({ isActive, isPending }) => isPending ? "pending" : isActive ? "active" : ""}> Contact </NavLink>
        </span>
        <span className='flex px-[16px] py-[14px] gap-[10px] items-center text-lg hover:bg-[#f2f2f2] hover:text-[#008000]'>
          <FaUserCheck/>
          <NavLink to="/login" className={({ isActive, isPending }) => isPending ? "pending" : isActive ? "active" : ""}> Login </NavLink>
        </span>
        {/* <span className='flex px-[16px] py-[14px] gap-[15px] items-center text-lg hover:bg-[#f2f2f2] hover:text-[#008000]'>
          <FaUserTimes/>
          <NavLink to="/" className={({ isActive, isPending }) => isPending ? "pending" : isActive ? "active" : ""}> Sign Up </NavLink>
        </span> */}
      </nav>

      <div className='mx-[auto] my-[24px] max-w-[700px] h-[400px] border border-black rounded-3xl bg-center animate-bannerImage'>
      </div>

      <div className='content-none clear-both table'>
        <div className='float-left w-3/4'>
          <div className="bg-white p-[20px]">
            <h2 className='font-bold text-xl'>WHAT YOU CAN DO HERE</h2>
            <div className="bg-[#90ee905e] w-full min-h-[200px]">
              <p className='p-[20px] tracking-normal'>
                On this website 'Farmer-Merchant Server', farmers can view their daily vegetables' sales, total sales, advance amount taken, etc. Merchants can view their generated slip for the vegetables they have bought, and the previous payments they have made, if any. Also, anyone can view the market rate of different vegetables and interested farmer or merchant can take benefit of this.
              </p>
            </div>
          </div>

          <div className="bg-white p-[20px]">
            <h2 className='font-bold text-xl'>ABOUT US</h2>
            <div className="bg-[#90ee905e] w-full min-h-[200px]">
              <p className='p-[20px] tracking-wide'>
                Here, we simply maintain daily record of sales and further process those data to generate bills for the merchants.
              </p>
            </div>
          </div>
        </div>

        <div className='float-left w-1/4'>
          <div className="bg-white p-[20px]">
            <h2 className='font-bold text-xl'>Market Rate</h2>
            <div className="bg-[#90ee905e] w-full h-[470px] overflow-hidden">
              <div className="w-[90%] mx-auto my-4">
                <table className="table-auto">
                  <thead className="text-red-500 text-lg font-bold">
                    <tr>
                      <th>
                        S.No.
                      </th>
                      <th>
                        Vegetable
                      </th>
                      <th>
                        Average Rate
                      </th>
                    </tr>
                  </thead>

                  <tbody>
                    {vegData?.data.map((vegetable) => (
                      <tr key={vegetable.veg_id} className="even:bg-gray-100 text-center text-sm">
                        <td className="p-2 text-gray-500">
                          {count}
                        </td>
                        <td className="p-2 text-gray-900">
                          {vegetable.veg_name}
                        </td>
                        <td className="p-2 text-gray-500">
                          {vegetable.rate}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>

      </div>

      <div className='flex bg-[#008000] text-white items-center flex-col'>
        {/* <button>Top</button> */}
        <p className='font-bold text-lg'>Contact us at </p>
        <p>Phone : <a href="tel:+919852449826" title="click to call">+91 9852449826</a></p>
        <p>Email : <a href="mailto:narendra0@yahoo.com" title="click to email">narendra0@yahoo.com</a></p>
      </div>

    </>
  )
}

export default HomePage;
