import React from "react";
import Clock from 'react-live-clock';
import { NavLink } from "react-router-dom";
import logo from '../images/favicon.png';
import "../index.css";
import { FcAlarmClock} from "react-icons/fc";
import {FaHome,FaPhoneSquareAlt} from "react-icons/fa";
import {FiMenu} from "react-icons/fi";
//FaUserCheck,FaUserTimes
import { useForm } from "react-hook-form";
// import { useNavigate } from "react-router-dom";
// import { toast } from "react-hot-toast";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Login = () => {

  const { register, handleSubmit } = useForm();
  // const navigate = useNavigate();

  const loginDetails = async (data) => {

    const savedUserResponse = await fetch(
      `${process.env.REACT_APP_BASE_URL}/login`,
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ ...data }),
      }
    );
    console.log("FORM RESPONSE......", savedUserResponse);
    
  };
  function showToastMessage() {
    toast.success('Success Notification !', {
        position: toast.POSITION.TOP_RIGHT
    });
};
  return (
    <>

      <div className="flex justify-between text-center items-center p-[30px]">
        <img src={logo} alt="{logo}" className='w-[60px] h-[60px]'/>
        <h1 className="text-[#008000] text-[50px] font-bold">Farmer-Merchant Server</h1>
        <div className="flex items-center">
          <FcAlarmClock className="text-xl mr-[10px]"/>
          <Clock format={'hh:mm:ss A'} ticking={true} className='text-md'/>
          {/* <span className="text-2xl">{`${new Date().toLocaleString()}`}</span> */}
        </div>
      </div>

      <nav className="flex mt-[10px] bg-[#008000] font-bold text-[#f2f2f2] overflow-hidden">
        <span className='flex px-[16px] py-[14px] gap-[10px] items-center text-lg hover:bg-[#f2f2f2] hover:text-[#008000]'>
          <FaHome/>
          <NavLink to="/" className={({ isActive, isPending }) => isPending ? "pending" : isActive ? "active" : ""}> Home </NavLink>
        </span>
        <span className='flex px-[16px] py-[14px] gap-[10px] items-center text-lg hover:bg-[#f2f2f2] hover:text-[#008000]'>
          <FiMenu/>
          <NavLink to="/" className={({ isActive, isPending }) => isPending ? "pending" : isActive ? "active" : ""}> About </NavLink>
        </span>
        <span className='flex px-[16px] py-[14px] gap-[10px] items-center text-lg hover:bg-[#f2f2f2] hover:text-[#008000]'>
          <FaPhoneSquareAlt/>
          <NavLink to="/" className={({ isActive, isPending }) => isPending ? "pending" : isActive ? "active" : ""}> Contact </NavLink>
        </span>
      </nav>

      <div className="p-[4rem] my-[2rem] mx-auto border border-[#1FE65B] rounded-lg w-[35%] shadow-bsd">
        <form onSubmit={handleSubmit(loginDetails)}>
          <div className=''>
            <div className="flex justify-between">
              <label htmlFor="email" className='font-bold'>{" "}Username{" "}</label>
              <input className="w-[200px] p-2 h-[2rem] border border-[#1FE65B] text-gray-600 rounded" type="text" placeholder="Enter your email" {...register("email")} />
            </div>
            <div className="flex justify-between mt-2">
              <label htmlFor="password" className='font-bold'>{" "}Password{" "}</label>
              <input className="w-[200px] p-2 h-[2rem] border border-[#1FE65B] text-gray-600 rounded" type="password" placeholder="Enter your password" {...register("password")} />
            </div>
            <div className="flex justify-between mt-2">
              <label htmlFor="role" className='font-bold'>{" "}User Type{" "}</label>
              <select className="w-[200px] h-[30px] border border-[#1FE65B] text-gray-600 rounded " {...register("role")} >
                <option value="admin">Admin</option>
                <option value="farmer">Farmer</option>
                <option value="merchant">Merchant</option>
              </select>
            </div>
            <div>
              <button onClick={toast.success("Login successful")} type="submit" className='bg-[#49E8A6] p-[0.5rem] cursor-pointer border-[3px] border-[turquoise] rounded mt-8 w-full hover:bg-[#08EE8E] transition-all'>Continue to Login</button>
            </div>
          </div>
        </form>
      </div>
      <div className='flex bg-[#008000] text-white items-center flex-col'>
        <p className='font-bold text-lg'>Contact us at </p>
        <p>Phone : <a href="tel:+919852449826" title="click to call">+91 9852449826</a></p>
        <p>Email : <a href="mailto:narendra0@yahoo.com" title="click to email">narendra0@yahoo.com</a></p>
      </div>

    </>
  )
}

export default Login;
