import React from "react";
import Clock from 'react-live-clock';
import { NavLink } from "react-router-dom";
import logo from '../images/favicon.png';
import "../index.css";
import { FcAlarmClock} from "react-icons/fc";
import {FaHome,FaPhoneSquareAlt} from "react-icons/fa";
import {FiMenu} from "react-icons/fi";
//FaUserCheck,FaUserTimes
import { useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";
// import { toast } from "react-hot-toast";
// import { ToastContainer, toast } from 'react-toastify';
// import 'react-toastify/dist/ReactToastify.css';

const Login = () => {
const navigate = useNavigate();
  function toaster(){
    const toaster = "Login successful";
    return toaster;
  }
  const { register, handleSubmit } = useForm();
  // const navigate = useNavigate();

  const loginDetails = async (data) => {

    const savedUserResponse = await fetch(
      `${process.env.REACT_APP_BASE_URL}/login`,
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ ...data }),
      }
    );
    console.log("FORM RESPONSE......", savedUserResponse);
    if(savedUserResponse.status === 200){
      alert('Login successful');
      navigate('/admin/dashboard');
    }
  };

  return (
    <>

<div className="header">
        <img src={logo} alt="{logo}"/>
        <h1>Farmer-Merchant Server</h1>
        <div className="datetime">
          <FcAlarmClock className="clock"/>
          <Clock format={'hh:mm:ss A'} ticking={true}/>
        </div>
      </div>

      <nav className="navbar">
        <span>
          <i className="fa fa-home cls"></i>
          <NavLink to="/" className="navlink"> Home </NavLink>
        </span>
        <span>
          <i className="fa fa-bars cls"></i>
          <NavLink to="/" className="navlink"> About </NavLink>
        </span>
        <span>
          <i className="fa fa-phone-square cls"></i>
          <NavLink to="/" className="navlink"> Contact </NavLink>
        </span>
      </nav>
      {success ? (
        <section>
          <h3>You are logged in!</h3>
        </section>
      ) : (
      <div className="login-container">
      <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
        <form onSubmit={handleSubmit}>
            <div className="login-section">
              <label htmlFor="email">Username</label>
              <input className="boxes" type="email" ref={userRef} id="email" placeholder="Enter your email" onChange={(e) => setUser(e.target.value)} value={user} required/>
            </div>
            <div className="login-section">
              <label htmlFor="password">Password</label>
              <input className="boxes" type="password" id="password" placeholder="Enter your password" onChange={(e) => setPwd(e.target.value)} value={pwd} required/>
            </div>
            <div className="login-section">
              <label htmlFor="role">User Type</label>
              <select className="boxes" onChange={(e) => setRole(e.target.value)} value={role} >
                <option value="admin">Admin</option>
                <option value="farmer">Farmer</option>
                <option value="merchant">Merchant</option>
              </select>
            </div>
            <div>
              <button type="submit" className='submit'>Continue to Login</button>
            </div>
        </form>
      </div>
      )}
      <div className='footer'>
        <p className='contact'>Contact us at </p>
        <p>Phone : <a href="tel:+919852449826" title="click to call">+91 9852449826</a></p>
        <p>Email : <a href="mailto:narendra0@yahoo.com" title="click to email">narendra0@yahoo.com</a></p>
      </div>

    </>
  )
}

export default Login;
